import  { BrowserMultiFormatReader }  from '@zxing/browser';
import { useEffect, useRef} from 'react';
import './App.css';

function App() {
  const videoRef = useRef(null);
  const controlsRef = useRef(null);
  const [barcode, setBarcode] = useState(null);
  const [scanning, setScanning] = useState(true);
  const [bookInfo, setBookInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);



  useEffect(() => {
    const codeReader = new BrowserMultiFormatReader();
    const startScanner = () => {
      if (!videoRef.current) return;

      codeReader.decodeFromVideoDevice(null, videoRef.current, (result, err, controls) => {
          if (!controlsRef.current) {
            controlsRef.current = controls; 
          }

          if (result) {
            console.log("Scanned result:", result.getText());
            setBarcode(result)
          }

          if (err && err.name !== 'NotFoundException') {
            console.err("Scan error:", err);
            }
          }
        );
      };

    requestAnimationFrame(startScanner);



    return () => {
      if (controlsRef.current) {
      controlsRef.current.stop(); // Stop the scanner on unmount
      }
    };
  }, []);

  return (
    <div>
      <video
        ref={videoRef}
        autoPlay
        muted
        playsInline
        style={{ width: '100%', objectFit: 'cover', backgroundColor: 'black' }}
      />
    </div>
  );
}

export default App;
